import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Row, Col } from 'react-bootstrap';
import MapIcon from '../../Image/Group1.svg'; 
import PhoneIcon from '../../Image/Group2.svg';
import './AboutPage.css'; 

const AboutPage = () => {
  const [companyInfo, setCompanyInfo] = useState({ title:'', description: '', location: '', phoneNumber: '' });


  useEffect(() => {
    axios.get('http://localhost:3001/api/company') 
      .then(response => {
        setCompanyInfo(response.data);
      })
      .catch(error => console.error('Ошибка при получении данных: ', error));
  }, []);

  return (
    <Container className="about-section my-5">
      <Row className="align-items-center">
        <Col xs={12} md={6} className="text-center text-md-left">
          <div className="contact-info">
            <p><img src={MapIcon} alt="Location" className="icon" /> {companyInfo.location}</p>
            <p><img src={PhoneIcon} alt="Phone" className="icon" /> {companyInfo.phoneNumber}</p>
          </div>
        </Col>
        <Col xs={12} md={6}>
        </Col>
      </Row>
    </Container>
  );
};

export default AboutPage;
