import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { useCart } from '../CartContext/CartContext';
import { Link, useLocation } from 'react-router-dom'; 
import './Catalog.css';

function Catalog() {
    const { addToCart } = useCart();
    const [products, setProducts] = useState([]);
    const [addedToCart, setAddedToCart] = useState(false);
    const [loading, setLoading] = useState(false); // Новое состояние для индикатора загрузки
    const [error, setError] = useState(null);
    const location = useLocation();
    const isHomePage = location.pathname === '/';

    useEffect(() => {
        const fetchProducts = async () => {
            setLoading(true); // Начало загрузки
            try {
                const token = localStorage.getItem('token');
                const response = await fetch('/api/products', {
                    headers: token ? { 'Authorization': `Bearer ${token}` } : {},
                });

                if (!response.ok) {
                    throw new Error(`Ошибка HTTP, статус: ${response.status}`);
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false); // Загрузка завершена
            }
        };

        fetchProducts();
    }, []);

    const handleAddToCart = async (product) => {
        try {
            setLoading(true); // Блокировка кнопки во время обработки
            addToCart(product);
            setAddedToCart(true);
            setTimeout(() => setAddedToCart(false), 2000);
        } catch (error) {
            setError('Не удалось добавить товар в корзину.');
        } finally {
            setLoading(false); // Снимаем блокировку с кнопки
        }
    };
    if (loading) {
        return <Alert variant="info">Загрузка...</Alert>;
    }

    if (error) {
        return <Alert variant="danger">Ошибка: {error}</Alert>;
    }


    // Ограничиваем количество продуктов на главной странице
    const displayedProducts = isHomePage ? products.slice(0, 3) : products;



    return (
        <Container className="catalog-container my-5">
            {addedToCart && <Alert variant="success">Товар добавлен в корзину!</Alert>}
            <Row>
                <Col xs={12} className="text-center mb-4">
                    <h2>Каталог товаров</h2>
                </Col>
            </Row>
            <Row className="justify-content-center">
                {displayedProducts.map(product => (
                    <Col key={product.id} xs={12} sm={6} md={4} lg={3} className="mb-4 d-flex align-items-stretch">
                        <Card className="product-card">
                            <Card.Img variant="top" src={product.image} alt={product.title} />
                            <Card.Body className="d-flex flex-column">
                                <Card.Title>{product.title}</Card.Title>
                                <Card.Text className="mt-auto">
                                    {`${product.price} Р`}
                                </Card.Text>
                                <Button variant="success" className="mt-auto" onClick={() => handleAddToCart(product)}>
                                    Купить
                                </Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
            {isHomePage && products.length > 3 && (
                <Row>
                    <Col className="text-center">
                    <Button 
                variant="outline-primary" 
                as={Link} 
                to="/catalog"
                className="show-more-button" // Применяем новый класс к кнопке
            >
                Посмотреть больше
            </Button>
                    </Col>
                </Row>
            )}
        </Container>
    );
}

export default Catalog;
