import React, { useState } from 'react';
import { useAuth } from '../AuthContext/AuthContext';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true); // Состояние для переключения между входом и регистрацией
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAuth(); // Используйте соответствующие функции аутентификации и регистрации

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    if (!username || !password) {
      setError('Пожалуйста, заполните все поля.');
      return;
    }

    try {
      if (isLogin) {
        // Логика для входа в систему
        await login(username, password);
      } else {
        // Логика для регистрации
        // await register(username, password);
      }
    } catch (authError) {
      setError(isLogin ? 'Ошибка при входе.' : 'Ошибка при регистрации.');
    }
  };

  return (
    <div>
      <h2>{isLogin ? 'Вход в систему' : 'Регистрация'}</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Имя пользователя:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div>
          <label htmlFor="password">Пароль:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button type="submit">{isLogin ? 'Войти' : 'Зарегистрироваться'}</button>
      </form>
      <button onClick={() => setIsLogin(!isLogin)}>
        {isLogin ? 'Нет аккаунта? Зарегистрируйтесь' : 'Уже есть аккаунт? Войдите'}
      </button>
    </div>
  );
};

export default AuthForm;
