import React, { createContext, useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const CartContext = createContext();

export const useCart = () => useContext(CartContext);

const getUserIdFromToken = () => {
  const token = localStorage.getItem('token');
  if (!token) return null;
  try {
    const payload = JSON.parse(atob(token.split('.')[1]));
    return payload.userId;
  } catch (error) {
    console.error("Ошибка при обработке токена:", error);
    return null;
  }
};

export const CartProvider = ({ children }) => {
  const navigate = useNavigate();
  const [userId, setUserId] = useState(getUserIdFromToken());
  const [isUserAuthenticated, setIsUserAuthenticated] = useState(!!userId);
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    const handleUserChange = () => {
      const newUserId = getUserIdFromToken();
      setIsUserAuthenticated(!!newUserId);
      setUserId(newUserId);
      const savedCart = localStorage.getItem(`cart-${newUserId || 'anonymous'}`);
      setCartItems(savedCart ? JSON.parse(savedCart) : []);
    };

    handleUserChange(); // Вызываем при инициализации для установки корзины текущего пользователя

    // Подписываемся на события изменения токена (например, при входе/выходе пользователя)
    window.addEventListener('storage', handleUserChange);
    return () => window.removeEventListener('storage', handleUserChange); // Отписываемся при размонтировании компонента
  }, []);

  useEffect(() => {
    // Сохраняем текущее состояние корзины в localStorage каждый раз при изменении userId или cartItems
    if (userId) {
      localStorage.setItem(`cart-${userId}`, JSON.stringify(cartItems));
    }
  }, [userId, cartItems]);

  const addToCart = (product) => {
    setCartItems(prevItems => {
      const itemIndex = prevItems.findIndex(item => item.id === product.id);
      if (itemIndex !== -1) {
        const updatedItems = [...prevItems];
        updatedItems[itemIndex].quantity += 1;
        return updatedItems;
      }
      return [...prevItems, { ...product, quantity: 1 }];
    });
  };

  const updateQuantity = (productId, quantity) => {
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.id === productId ? { ...item, quantity: Math.max(0, quantity) } : item // Позволяет количество быть уменьшенным до 0
      )
    );
  };

  const removeItem = (productId) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== productId));
  };

  const placeOrder = async () => {
    if (!isUserAuthenticated) {
      navigate('/login');
      return;
    }

    if (cartItems.length === 0) {
      alert('Ваша корзина пуста.');
      return;
    }

    const token = localStorage.getItem('token');
    try {
      const response = await fetch('/api/orders/placeOrder', {
        method: 'POST',
        body: JSON.stringify({ cartItems }),
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
      });

      if (!response.ok) {
        throw new Error(`Ошибка: ${response.statusText}`);
      }

      setCartItems([]); // Очищаем корзину после успешного оформления заказа
      alert('Заказ успешно оформлен!');
    } catch (error) {
      console.error('Ошибка при оформлении заказа:', error);
      alert('Ошибка при оформлении заказа.');
    }
  };

  return (
    <CartContext.Provider value={{
      cartItems,
      addToCart,
      updateQuantity,
      removeItem,
      placeOrder,
      isUserAuthenticated,
    }}>
      {children}
    </CartContext.Provider>
  );
};

export default CartContext;
