import React, { useState } from 'react';

const FurnitureConstructor = () => {
  const [type, setType] = useState('ТВ-тумба');
  const [dimensions, setDimensions] = useState({ width: 1075, height: 1000, depth: 300 });
  const [color, setColor] = useState('Белый');
  const [material, setMaterial] = useState('ЛДСП');
  const [shelves, setShelves] = useState(3);
  const [drawers, setDrawers] = useState(3);
  const [price, setPrice] = useState(17019); // Здесь должна быть логика для расчета цены

  const handleDimensionChange = (dimension, value) => {
    setDimensions({ ...dimensions, [dimension]: value });
  };

  return (
    <div>
      <h2>Конструктор мебели</h2>
      {/* Selector for furniture type */}
      <div>
        <label>Выберите тип мебели:</label>
        <select value={type} onChange={(e) => setType(e.target.value)}>
          <option value="ТВ-тумба">ТВ-тумба</option>
          <option value="Комод">Комод</option>
          <option value="Шкаф-купе">Шкаф-купе</option>
        </select>
      </div>

      {/* Sliders for dimensions */}
      <div>
        <label>Ширина:</label>
        <input type="range" min="600" max="1500" value={dimensions.width} onChange={(e) => handleDimensionChange('width', parseInt(e.target.value))} />
        {dimensions.width} мм
      </div>
      <div>
        <label>Высота:</label>
        <input type="range" min="400" max="2000" value={dimensions.height} onChange={(e) => handleDimensionChange('height', parseInt(e.target.value))} />
        {dimensions.height} мм
      </div>
      <div>
        <label>Глубина:</label>
        <input type="range" min="200" max="600" value={dimensions.depth} onChange={(e) => handleDimensionChange('depth', parseInt(e.target.value))} />
        {dimensions.depth} мм
      </div>

      {/* Selectors for color and material */}
      <div>
        <label>Цвет:</label>
        <select value={color} onChange={(e) => setColor(e.target.value)}>
          <option value="Белый">Белый</option>
          <option value="Черный">Черный</option>
          <option value="Дуб">Дуб</option>
        </select>
      </div>
      <div>
        <label>Материал:</label>
        <select value={material} onChange={(e) => setMaterial(e.target.value)}>
          <option value="ЛДСП">ЛДСП</option>
          <option value="МДФ">МДФ</option>
          <option value="Стекло">Стекло</option>
        </select>
      </div>

      {/* Number selectors for shelves and drawers */}
      <div>
        <label>Количество полок:</label>
        <input type="number" min="0" max="10" value={shelves} onChange={(e) => setShelves(parseInt(e.target.value))} />
      </div>
      <div>
        <label>Количество ящиков:</label>
        <input type="number" min="0" max="10" value={drawers} onChange={(e) => setDrawers(parseInt(e.target.value))} />
      </div>

      {/* Price and Add to cart button */}
      <div>
        <p>Цена: {price} Р</p>
        <button onClick={() => alert('Добавлено в корзину')}>Добавить в корзину</button>
      </div>
    </div>
  );
};

export default FurnitureConstructor;
