import React, { useEffect, useState } from 'react';
import { useCart } from '../CartContext/CartContext';
import CartItem from "./CartItem/CartItem";
import PlaceOrderComponent from '../PlaceOrder/PlaceOrder';
import { useNavigate } from 'react-router-dom';
import "./Cart.css";

function Cart() {
  const { cartItems, removeItem, placeOrder, updateQuantity } = useCart();
  const [isUserAuthenticated, setIsUserAuthenticated] = useState(false);
  const navigate = useNavigate();

  // Рассчитываем общую стоимость корзины
  const totalCost = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  // Проверяем статус авторизации пользователя
  useEffect(() => {
    const userStatus = checkUserAuthentication(); // Эта функция должна определить, авторизован ли пользователь
    setIsUserAuthenticated(userStatus);
  }, []);

  // Функция для обновления количества товара в корзине
  const handleUpdateQuantity = (productId, newQuantity) => {
    updateQuantity(productId, newQuantity);
  };

  // Функция для удаления товара из корзины
  const handleRemoveItem = (productId) => {
    removeItem(productId);
  };

  // Функция для перенаправления пользователя обратно в каталог
  const handleBackToShopping = () => {
    navigate('/catalog');
  };

  return (
    <div className="cart-container">
      <h2 className="cart-title">Ваша корзина</h2>
      <div className="cart-items-list">
        {cartItems.length > 0 ? (
          <>
            {cartItems.map(item => (
              <CartItem 
                key={item.id}
                item={item}
                updateQuantity={handleUpdateQuantity}
                removeItem={handleRemoveItem}
              />
            ))}
            <div className="cart-checkout">
              <div className="cart-total">
                <strong>Общая стоимость:</strong>
                <span>{totalCost.toLocaleString('ru-RU', { style: 'currency', currency: 'RUB' })}</span>
              </div>
              {/* Показываем компонент оформления заказа только если пользователь авторизован */}
              {isUserAuthenticated && <PlaceOrderComponent placeOrder={placeOrder} />}
            </div>
          </>
        ) : (
          <div className="cart-empty-message">
            <p>Ваша корзина пуста.</p>
            <button 
              className="btn-back-to-shopping" 
              onClick={handleBackToShopping}
            >
              Вернуться к покупкам
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Cart;
