import React, { createContext, useContext, useState } from 'react';

export const AuthContext = createContext(null);

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleAuthResponse = async (response) => {
    const data = await response.json();
    if (!response.ok) {
      if (response.status === 400) {
        throw new Error('Не валидные данные');
      } else if (response.status === 409) {
        throw new Error('Такой пользователь уже есть в системе');
      } else {
        throw new Error(data.message || 'Неверный логин или пароль');
      }
    }
    return data;
  };

  const login = async (username, password) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/users/logins', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await handleAuthResponse(response);
      setUser({ name: username, role: data.role });
      localStorage.setItem('token', data.token);
    } catch (error) {
      setError(error.message);
      setUser(null);
    } finally {
      setIsLoading(false);
    }
  };
  const register = async (username, password) => {
    setIsLoading(true);
    setError(null);
    try {
    const response = await fetch('/api/users/registers', {
    method: 'POST',
    headers: {
    'Content-Type': 'application/json',
    },
    body: JSON.stringify({ username, password }),
    });
    const data = await handleAuthResponse(response);
    setUser({ name: username, role: data.role });
    localStorage.setItem('token', data.token);
  } catch (error) {
    setError(error.message);
    setUser(null);
  } finally {
    setIsLoading(false);
  }
};

const logout = () => {
setUser(null);
localStorage.removeItem('token');
setError(null);
};

return (
<AuthContext.Provider value={{ user, isLoading, error, login, register, logout }}>
{children}
</AuthContext.Provider>
);
};

export default AuthContext;
