import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({
        title: '',
        sections: [], // Для текстовых разделов
        images: []    // Для изображений
    });
    const [error, setError] = useState(null);

    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:3000/api/introSection');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();

            // Устанавливаем полученные данные в состояние
            setIntroData({
                title: data.title,
                sections: data.sections || [],
                images: data.images || [] // Убедитесь, что images обрабатываются как массив
            });
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    // Рендерим заголовок, секции и изображения
    return (
        <section className="intro-section">
            <h2>{introData.title}</h2>
            {introData.sections.map((section, index) => (
                <div key={index}>
                    <h3>{section.header}</h3>
                    <p>{section.content}</p>
                </div>
            ))}
            <div className="gallery">
                {introData.images.map((img, index) => (
                    <div className="image-container" key={index}>
                        <img src={img} alt={`Gallery image ${index + 1}`} />
                    </div>
                ))}
            </div>
        </section>
    );
}

export default IntroSection;
