// App.js
import React, { Suspense, lazy } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './components/AuthContext/AuthContext'; // Убедитесь, что путь правильный
import { CartProvider } from './components/CartContext/CartContext';

const Header = lazy(() => import('./components/Header/Header'));
const SliderComponent = lazy(() => import('./components/SliderComponent/SliderComponent'));
const IntroSection = lazy(() => import('./components/IntroSection/IntroSection'));
const Catalog = lazy(() => import('./components/Catalog/Catalog'));
const Footer = lazy(() => import('./components/Footer/Footer'));
const AboutPage = lazy(() => import('./components/AboutPage/AboutPage'));
const Cart = lazy(() => import('./components/Cart/Cart'));
const NotFoundPage = lazy(() => import('./components/NotFoundPage/NotFoundPage'));

const IndexPage = () => (
  <>
    <SliderComponent />
    <Catalog />
    <AboutPage />
  </>
); 

function App() {
  return (
    <AuthProvider> {/* Добавлен AuthProvider */}
      <CartProvider>
        <Router>
          <div className="App">
            <Suspense fallback={<div>Loading...</div>}>
              <Header />
              <div className="content-wrapper">
                <main>
                  <Routes>
                    <Route path="/about" element={<IntroSection />} />
                    <Route path="/catalog" element={<Catalog />} />
                    <Route path="/cart" element={<Cart />} />
                    <Route path="/profile" element={<UserProfile />} />
                    <Route path="/" element={<IndexPage />} />
                    <Route path="*" element={<NotFoundPage />} /> {/* 404 страница */}
                  </Routes>
                </main>
              </div>
              <Footer />
            </Suspense>
          </div>
        </Router>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
