import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
  const [introData, setIntroData] = useState(null);

  useEffect(() => {
    fetch('/api/IntroSection')
      .then(response => response.json())
      .then(data => setIntroData(data))
      .catch(error => console.error('Error:', error));
  }, []);

  if (!introData) return <div>Loading...</div>;

  return (
    <section className="IntroSection">
      <h2>{introData.title}</h2>
      {introData.paragraphs.map((paragraph, index) => (
        <p key={index}>{paragraph}</p>
      ))}
      <div className="gallery">
        {introData.images.map((img, index) => (
          <img key={index} src={img} alt={`Изображение ${index}`} />
        ))}
      </div>
    </section>
  );
}

export default IntroSection;
