import React from 'react';
import { useFurniture } from '../context/FurnitureContext';

const MaterialSelector = () => {
    const { selectedMaterial, setSelectedMaterial, materials } = useFurniture();

    return (
        <div className="selector material-selector">
            <h3>Выберите материал</h3>
            <div className="materials">
                {materials.map(material => (
                    <label key={material.material_id} className={`material-option ${selectedMaterial === material.name ? 'selected' : ''}`}>
                        <input 
                            type="radio" 
                            name="material" 
                            value={material.name} 
                            checked={selectedMaterial === material.name} 
                            onChange={e => setSelectedMaterial(e.target.value)}
                        />
                        {material.name}
                    </label>
                ))}
            </div>
        </div>
    );
};

export default MaterialSelector;
