import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [sections, setSections] = useState([]);
    const [selectedSection, setSelectedSection] = useState(null);
    const [modalVisible, setModalVisible] = useState(false);
    const [error, setError] = useState(null);

    const fetchData = async () => {
        try {
            const response = await fetch('/api/introSection');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();
            setSections(data); // Предполагаем, что data - это массив разделов
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    const openModal = (section) => {
        setSelectedSection(section);
        setModalVisible(true);
    };

    const closeModal = () => {
        setModalVisible(false);
    };

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            {sections.map((section, index) => (
                <div key={index} className="section-block">
                    <div className="image-block" onClick={() => openModal(section)}>
                        <img src={section.image_url} alt={`Изображение раздела ${index + 1}`} />
                    </div>
                    <h3>{section.title}</h3>
                    <button onClick={() => openModal(section)}>Подробнее</button>
                </div>
            ))}
            {modalVisible && (
                <div className="modal" onClick={closeModal}>
                    <div className="modal-content" onClick={e => e.stopPropagation()}>
                        <h3>{selectedSection?.title}</h3>
                        <p>{selectedSection?.content}</p>
                        <button onClick={closeModal}>Закрыть</button>
                    </div>
                </div>
            )}
        </section>
    );
}

export default IntroSection;
