import React, { useState } from 'react';
import MaterialSelector from './MaterialSelector';
import SizeSelector from './SizeSelector';
import CustomizationSelector from './CustomizationSelector';
import { useFurniture } from '../context/FurnitureContext'; // Проверьте пути
import '../Style/FurnitureConstructor.css'; // Проверьте пути

const FurnitureConstructor = () => {
    const { material, size, customizations } = useFurniture();
    const [productImage] = useState(''); // Добавьте состояние для изображения продукта

    const handleSubmit = async (event) => {
        event.preventDefault();
        // Здесь должен быть ваш API вызов для отправки данных
        console.log({ material, size, customizations });

        // Предположим, что у вас есть функция API для отправки заказа
        // const response = await submitOrder({ material, size, customizations });
        // setProductImage(response.imageUrl); // Предположим, что ответ содержит URL изображения
    };

    return (
        <form onSubmit={handleSubmit} className="furniture-constructor-form">
            <h2>Создайте свою мебель</h2>
            <MaterialSelector />
            <SizeSelector />
            <CustomizationSelector />
            <button type="submit">Подтвердить дизайн</button>
            {productImage && <img src={productImage} alt="Custom Furniture" className="product-image" />}
        </form>
    );
};

export default FurnitureConstructor;
