import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({
        title: '',
        paragraphs: [],
        images: [] // Убедитесь, что это изначально массив
    });
    const [error, setError] = useState(null);

    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:3000/api/introSection');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();

            // Проверка и преобразование images в массив, если он пришел в виде строки
            const imagesArray = Array.isArray(data.images) ? data.images : data.images.split(',');

            setIntroData({
                title: data.title,
                paragraphs: data.paragraphs || [],
                images: imagesArray
            });
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    // Отображение контента раздела
    return (
        <section className="intro-section">
            <h2>{introData.title || 'Портфолио'}</h2>
            <div className="gallery">
                {Array.isArray(introData.images) && introData.images.map((img, index) => (
                    <div className="image-container" key={index}>
                        <img src={img} alt={`Изображение ${index + 1}`} />
                        <h3 className="image-text">{introData.paragraphs[index]?.header}</h3>
                    </div>
                ))}
            </div>
        </section>
    );
}

export default IntroSection;
