import React, { createContext, useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import jwt_decode from 'jwt-decode';  // Corrected import

const CartContext = createContext();

export const useCart = () => useContext(CartContext);

const getUserIdFromToken = () => {
    const token = localStorage.getItem('token');
    if (!token) return null;
    try {
        const decodedToken = jwt_decode(token); // Corrected usage
        return decodedToken.user_id || decodedToken.sub; // Ensure this matches your token structure
    } catch (error) {
        console.error("Error decoding token:", error);
        return null;
    }
};

export const CartProvider = ({ children }) => {
    const navigate = useNavigate();
    const [userId, setUserId] = useState(getUserIdFromToken());
    const [cartItems, setCartItems] = useState([]);
    const [orderStatus, setOrderStatus] = useState(null); // Ensure this state is defined

    useEffect(() => {
        setUserId(getUserIdFromToken());
    }, []); // Added empty dependency array to avoid infinite updates

    useEffect(() => {
        const newUserId = getUserIdFromToken();
        if (newUserId !== userId) {
            setUserId(newUserId);
            const savedCart = localStorage.getItem(newUserId ? `cart-${newUserId}` : 'cart');
            setCartItems(savedCart ? JSON.parse(savedCart) : []);
        }
    }, [userId]); // Correct dependency

    useEffect(() => {
        if (userId) {
            localStorage.setItem(`cart-${userId}`, JSON.stringify(cartItems));
        }
    }, [userId, cartItems]);
  const addToCart = (product) => {
    setCartItems(prevItems => {
      const itemIndex = prevItems.findIndex(item => item.id === product.id);
      if (itemIndex !== -1) {
        const updatedItems = [...prevItems];
        updatedItems[itemIndex].quantity += 1;
        return updatedItems;
      }
      return [...prevItems, { ...product, quantity: 1 }];
    });
  };

  const updateQuantity = (productId, quantity) => {
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.id === productId ? { ...item, quantity: Math.max(1, quantity) } : item
      )
    );
  };

  const removeItem = (productId) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== productId));
  };

  const placeOrder = async () => {
    if (!isUserAuthenticated) {
      navigate('/login');
      return;
    }

    if (cartItems.length === 0) {
      setOrderStatus('empty');
      return;
    }

    setOrderStatus('processing');
    const token = localStorage.getItem('token');

    try {
      const response = await fetch('/api/orders/placeOrder', {
        method: 'POST',
        body: JSON.stringify({ items: cartItems }),
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
      });

      if (response.status === 429) {
        setOrderStatus('too-many-requests');
        return;
      }

      if (!response.ok) {
        throw new Error(`Ошибка: ${response.status}`);
      }

      setCartItems([]);
      setOrderStatus('success');
    } catch (error) {
      console.error('Ошибка при оформлении заказа:', error);
      setOrderStatus('error');
    }
  };

  return (
    <CartContext.Provider value={{
      cartItems,
      addToCart,
      updateQuantity,
      removeItem,
      placeOrder,
      isUserAuthenticated: !!userId, // Проверяем, аутентифицирован ли пользователь
    }}>
      {children}
    </CartContext.Provider>
  );
};

export default CartContext;
