import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Row, Col } from 'react-bootstrap';
import MapIcon from '../../Image/Group1.svg'; 
import PhoneIcon from '../../Image/Group2.svg';
import './AboutPage.css'; 

const AboutPage = () => {
  const [companyInfo, setCompanyInfo] = useState({ title:'', description: '', location: '', phoneNumber: '' });

  useEffect(() => {
    axios.get('http://localhost:3001/api/company')
      .then(response => {
        console.log(response.data); // Для отладки
        if(response.data && response.data.length > 0) {
          setCompanyInfo(response.data[0]); // Если данные возвращаются в виде массива
        }
      })
      .catch(error => console.error('Ошибка при получении данных: ', error));
  }, []);

  if (!companyInfo.title) {
    return <div>Загрузка данных...</div>;
  }

  return (
    <Container className="about-section my-5">
      <Row className="justify-content-center mb-4">
        <Col xs={12} className="text-center">
          <h2 className="about-title">{companyInfo.title}</h2>
          <p className="about-description">{companyInfo.description}</p>
        </Col>
      </Row>
      <Row className="align-items-center">
        <Col xs={12} md={6} className="text-center text-md-left">
          <div className="contact-info">
            <p><img src={MapIcon} alt="Location" className="icon" /> {companyInfo.location}</p>
            <p><img src={PhoneIcon} alt="Phone" className="icon" /> {companyInfo.phoneNumber}</p>
          </div>
        </Col>
        <Col xs={12} md={6}>
          {/* Здесь может быть ваш iframe для карты */}
        </Col>
      </Row>
    </Container>
  );
};

export default AboutPage;
