import React, { createContext, useState, useContext, useEffect } from 'react';

const CartContext = createContext();

export const useCart = () => useContext(CartContext);

export const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const [orderStatus, setOrderStatus] = useState(null);

  // Загрузка корзины из localStorage при инициализации
  useEffect(() => {
    const savedCart = localStorage.getItem('cart');
    if (savedCart) {
      setCartItems(JSON.parse(savedCart));
    }
  }, []);

  // Сохранение состояния корзины в localStorage при изменении
  useEffect(() => {
    localStorage.setItem('cart', JSON.stringify(cartItems));
  }, [cartItems]);

  // Проверка наличия токена для определения статуса аутентификации пользователя
  const isUserAuthenticated = () => {
    return localStorage.getItem('token') != null;
  };

  // Добавление товара в корзину
  const addToCart = (product) => {
    setCartItems(prevItems => {
      const existingItem = prevItems.find(item => item.id === product.id);
      if (existingItem) {
        // Увеличение количества существующего товара
        return prevItems.map(item => 
          item.id === product.id ? { ...item, quantity: item.quantity + 1 } : item
        );
      }
      // Добавление нового товара в корзину
      return [...prevItems, { ...product, quantity: 1 }];
    });
  };

  // Обновление количества товара в корзине
  const updateQuantity = (productId, quantity) => {
    setCartItems(prevItems => 
      prevItems.map(item => 
        item.id === productId ? { ...item, quantity: Math.max(1, quantity) } : item
      )
    );
  };

  // Удаление товара из корзины
  const removeItem = (productId) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== productId));
  };

  // Оформление заказа
  const placeOrder = () => {
    if (!isUserAuthenticated()) {
      setOrderStatus('unauthenticated');
      // Здесь можно добавить логику для перенаправления на страницу входа или отображения модального окна
      return;
    }

    if (cartItems.length === 0) {
      setOrderStatus('empty');
      // Отображение сообщения о пустой корзине
      return;
    }

    setOrderStatus('processing');
    const token = localStorage.getItem('token');

    // Отправка запроса на сервер для оформления заказа
    fetch('/api/orders/placeOrder', {
      method: 'POST',
      body: JSON.stringify({ items: cartItems }),
      headers: {
        'Content-Type': 'application/json',
        ...(token && { 'Authorization': `Bearer ${token}` })
      },
    })
    .then(response => {
      if (response.status === 429) {
        setOrderStatus('too-many-requests');
      } else if (!response.ok) {
        throw new Error(`Ошибка: ${response.status}`);
      }
      return response.json();
    })
    .then(data => {
      setCartItems([]);
      setOrderStatus('success');
    })
    .catch(error => {
      console.error('Ошибка при оформлении заказа:', error);
      setOrderStatus('error');
    });
  };



// Закрывающие скобки компонента CartProvider
return (
  <CartContext.Provider value={{
    cartItems,
    addToCart,
    updateQuantity,
    removeItem,
    placeOrder,
    orderStatus
  }}>
    {children}
  </CartContext.Provider>
);
};

export default CartContext;
