import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({
        title: '',
        sections: [],
        images: []
    });
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const response = await fetch('http://localhost:3000/api/introSection');
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                // Предполагая, что data уже содержит нужную структуру, прямо обновляем состояние
                setIntroData({
                    title: data.title,
                    sections: data.sections ? data.sections : [], // Проверка на существование и массив
                    images: data.images ? data.images : [], // Проверка на существование и массив
                });
            } catch (error) {
                console.error('Error fetching data:', error);
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            <h2>{introData.title}</h2>
            {introData.sections.map((section, index) => (
                <div key={index}>
                    <h3>{section.header}</h3>
                    <p>{section.content}</p>
                </div>
            ))}
            <div className="gallery">
                {introData.images.map((image, index) => (
                    <div className="image-container" key={index}>
                        {/* Убедитесь, что каждый элемент image является строкой URL. */}
                        <img src={image} alt={`Gallery item ${index + 1}`} loading="lazy" />
                    </div>
                ))}
            </div>
        </section>
    );
}

export default IntroSection;
