import React, { useState } from 'react';
import { useAuth } from '../AuthContext/AuthContext';


const AuthForm = () => {
  const [formType, setFormType] = useState('login'); // Стейт для определения типа формы
  const [credentials, setCredentials] = useState({ username: '', password: '', email: '', newPassword: '' });
  const { login, register, isLoading, error, updatePassword } = useAuth(); // Добавлен updatePassword в хуки

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCredentials(prevCredentials => ({ ...prevCredentials, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    if (isLoading) return;

    switch (formType) {
      case 'login':
        login(credentials.username, credentials.password);
        break;
      case 'register':
        register(credentials.username, credentials.password, credentials.email);
        break;
      case 'reset':
        updatePassword(credentials.username, credentials.newPassword);
        break;
      default:
        console.error('Неизвестный тип формы');
    }
  };

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">{formType === 'login' ? 'Вход в систему' : formType === 'register' ? 'Регистрация' : 'Сброс пароля'}</h2>
        <form onSubmit={handleSubmit}>
          {formType === 'register' && (
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={credentials.email}
                onChange={handleChange}
                required={formType === 'register'}
                disabled={isLoading}
              />
            </div>
          )}
          <div className="form-group">
            <label htmlFor="username">Имя пользователя</label>
            <input
              type="text"
              id="username"
              name="username"
              value={credentials.username}
              onChange={handleChange}
              required
              disabled={isLoading}
            />
          </div>
          {formType !== 'reset' && (
            <div className="form-group">
              <label htmlFor="password">Пароль</label>
              <input
                type="password"
                id="password"
                name="password"
                value={credentials.password}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>
          )}
          {formType === 'reset' && (
            <div className="form-group">
              <label htmlFor="newPassword">Новый пароль</label>
              <input
                type="password"
                id="newPassword"
                name="newPassword"
                value={credentials.newPassword}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>
          )}
          {error && <div className="alert alert-danger" role="alert">{error}</div>}
          <button type="submit" className="gradient-button">
            {isLoading ? 'Загрузка...' : formType === 'login' ? 'Войти' : formType === 'register' ? 'Зарегистрироваться' : 'Сбросить пароль'}
          </button>
          <button type="button" className="toggle-button" onClick={() => setFormType(formType === 'login' ? 'register' : 'login')}>
            {formType === 'login' ? 'Зарегистрироваться' : 'Войти'}
          </button>
          {formType !== 'reset' && (
            <button type="button" className="toggle-button" onClick={() => setFormType('reset')}>
              Забыли пароль?
            </button>
          )}
        </form>
      </div>
    </div>
  );
};

export default AuthForm;
