import React, { useState, useEffect } from 'react';
import './FurnitureConstructor.css';

const FurnitureConstructor = () => {
  const [size, setSize] = useState('');
  const [color, setColor] = useState('');
  const [material, setMaterial] = useState('');
  const [price, setPrice] = useState(0);
  const [image, setImage] = useState('default_image.jpg'); 
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    updateImage(size, material);
  }, [size, material]);

  const handleSizeChange = (e) => {
    setSize(e.target.value);
    updatePrice(e.target.value, material, color);
  };

  const handleColorChange = (e) => {
    setColor(e.target.value);
    updatePrice(size, material, e.target.value);
  };

  const handleMaterialChange = (e) => {
    setMaterial(e.target.value);
    updatePrice(size, e.target.value, color);
  };

  const updateImage = (selectedSize, selectedMaterial) => {
    setLoading(true);
    let imageUrl = `images/${selectedMaterial}_${selectedSize}.jpg`;
    setTimeout(() => {
      setImage(imageUrl);
      setLoading(false);
    }, 500); // Добавляем задержку для демонстрации загрузки
  };

  const updatePrice = (selectedSize, selectedMaterial, selectedColor) => {
    // ... (логика расчета цены без изменений)
  };

  const addToCart = () => {
    console.log('Товар добавлен в корзину: ', { size, color, material, price });
    // Добавьте логику для добавления товара в корзину
  };

  return (
    <div className="furniture-constructor">
      <div className="constructor-image">
        {loading ? <div>Загрузка...</div> : <img src={image} alt="Изображение мебели" />}
        <div className="current-selection">
          <p>Размер: {size || "не выбран"}</p>
          <p>Материал: {material || "не выбран"}</p>
          <p>Цвет: {color || "не выбран"}</p>
        </div>
      </div>

      <div className="constructor-form">
        <h3>Настройте вашу мебель</h3>
        {/* Форма для выбора параметров (без изменений) */}
        <p>Цена: {price} руб.</p>
        <button onClick={addToCart}>Добавить в корзину</button>
      </div>
    </div>
  );
};

export default FurnitureConstructor;
