import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({ paragraphs: [], images: [] });
    const [error, setError] = useState(null);

    useEffect(() => {
        fetch('/api/introSection')
            .then(response => {
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                return response.json();
            })
            .then(data => setIntroData(data))
            .catch(error => {
                console.error('Error fetching data:', error);
                setError(error.message);
            });
    }, []);

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            <h2>{introData.title || 'О нашей компании'}</h2>
            {introData.paragraphs.map((paragraph, index) => (
                <p key={index}>{paragraph}</p>
            ))}
            <div className="gallery">
                {introData.images.map((img, index) => (
                    <img key={index} src={img} alt={`Изображение ${index + 1}`} />
                ))}
            </div>
        </section>
    );
}

export default IntroSection;
