import React, { useState, useEffect } from 'react';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [editingUser, setEditingUser] = useState(null);
  const [error, setError] = useState(null);

  // Загрузка пользователей с сервера
  useEffect(() => {
    fetch('/api/users')
      .then(response => {
        if (!response.ok) {
          throw new Error(`Ошибка HTTP: ${response.status}`);
        }
        return response.json();
      })
      .then(data => setUsers(data))
      .catch(error => setError('Ошибка при загрузке пользователей'));
  }, []);

  // Начать редактирование пользователя
  const onEdit = user => setEditingUser({ ...user });

  // Обработка отправки формы редактирования
  const handleSubmit = e => {
    e.preventDefault();
    fetch(`/api/users/${editingUser.id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editingUser),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`Ошибка HTTP: ${response.status}`);
      }
      return response.json();
    })
    .then(updatedUser => {
      setUsers(users.map(user => user.id === updatedUser.id ? updatedUser : user));
      setEditingUser(null);
    })
    .catch(error => setError('Ошибка при обновлении пользователя'));
  };

  // Удаление пользователя
  const deleteUser = userId => {
    // Оптимистичное обновление UI
    const updatedUsers = users.filter(user => user.id !== userId);
    setUsers(updatedUsers);

    fetch(`/api/users/${userId}`, { method: 'DELETE' })
      .catch(error => {
        setError('Ошибка при удалении пользователя');
        // Восстановление данных в случае ошибки
        setUsers(users);
      });
  };

  return (
    <div className="admin-panel">
      {error && <div className="error-message">{error}</div>}
      {editingUser && (
        <form onSubmit={handleSubmit}>
          {/* Форма редактирования */}
        </form>
      )}
      <table>
        {/* Таблица пользователей */}
      </table>
    </div>
  );
};

export default AdminPanel;
