import React from 'react';
import { useCart } from '../CartContext/CartContext';
import CartItem from './CartItem/CartItem';
import './Cart.css';

function Cart() {
  const { cartItems, removeItem, placeOrder, updateQuantity, isUserAuthenticated } = useCart();

  const totalCost = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="cart-container">
      <h2 className="cart-title">Ваша корзина</h2>
      {cartItems.length > 0 ? (
        <div className="cart-items-list">
          {cartItems.map(item => (
            <CartItem 
              key={item.id}
              item={item}
              updateQuantity={updateQuantity}
              removeItem={removeItem}
            />
          ))}
          <div className="cart-total">
            <strong>Общая стоимость:</strong> {`${totalCost.toFixed(2)} руб.`}
          </div>
          <button className="cart-checkout-button" onClick={placeOrder}>
            Оформить заказ
          </button>
        </div>
      ) : (
        <p className="cart-empty-message">Ваша корзина пуста</p>
      )}
    </div>
  );
}

export default Cart;
