import React, { useState, useContext } from 'react';
import { AuthContext } from '../AuthContext/AuthContext'; 
import 'bootstrap/dist/css/bootstrap.min.css';
import './AuthForm.css';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user'); // Добавлено состояние для роли
  const { user, isLoading, error, login, register, logout } = useContext(AuthContext);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isLogin) {
      login(username, password);
    } else {
      register(username, password, role);
    }
  };

  const handleLogout = () => {
    logout();
    setUsername('');
    setPassword('');
    setRole('user'); // Сброс роли при выходе
  };

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">{isLogin ? 'Вход в систему' : 'Регистрация'}</h2>
        {error && <div className="alert alert-danger">{error}</div>}
        {user ? (
          <div>
            <div className="alert alert-success">Вы вошли как {user.name}</div>
            <button onClick={handleLogout} className="btn btn-danger">
              Выйти
            </button>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="auth-form">
            <div className="mb-3">
              <label htmlFor="username" className="form-label">Имя пользователя:</label>
              <input
                type="text"
                className="form-control"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password" className="form-label">Пароль:</label>
              <input
                type="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            {!isLogin && (
              <div className="mb-3">
                <label htmlFor="role" className="form-label">Роль:</label>
                <select
                  className="form-control"
                  id="role"
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                >
                  <option value="user">Пользователь</option>
                  <option value="admin">Администратор</option>
                </select>
              </div>
            )}
            <button type="submit" className="gradient-button btn btn-primary" disabled={isLoading}>
              {isLoading ? 'Загрузка...' : isLogin ? 'Войти' : 'Зарегистрироваться'}
            </button>
          </form>
        )}
        {!user && (
          <button onClick={() => setIsLogin(!isLogin)} className="toggle-button btn btn-link">
          {isLogin ? 'Нет аккаунта? Зарегистрируйтесь' : 'Уже есть аккаунт? Войдите'}
          </button>
          )}
          </div>
          </div>
          );
          };

          export default AuthForm;
