// App.js
import React, { Suspense, lazy } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { AuthProvider } from './components/AuthContext/AuthContext';
import { CartProvider } from './components/CartContext/CartContext';
import UserProfile from './components/UserProfile/UserProfile';
import FloatingIcons from "./components/NavEl/FloatingIcons"

const Header = lazy(() => import('./components/Header/Header'));
const SliderComponent = lazy(() => import('./components/SliderComponent/SliderComponent'));
const IntroSection = lazy(() => import('./components/IntroSection/IntroSection'));
const Catalog = lazy(() => import('./components/Catalog'));
const Footer = lazy(() => import('./components/Footer/Footer'));
const Cart = lazy(() => import('./components/Cart'));
const NotFoundPage = lazy(() => import('./components/NotFoundPage/NotFoundpage'));

const LoadingIndicator = () => (
  <div className="loading-indicator">
    <div className="spinner-border text-primary" role="status">
      <span className="sr-only">Загрузка...</span>
    </div>
  </div>
);

const IndexPage = () => (
  <>
    <SliderComponent />
    <Catalog />

  </>
);

function App() {
  return (
    <AuthProvider>
      <CartProvider>
        <Router>
          <div className="App">
          <Suspense fallback={<LoadingIndicator />}>
              <Header />
              <div className="content-wrapper">
                <main>
                  <Routes>
                    <Route path="/about" element={<IntroSection />} />
                    <Route path="/catalog" element={<Catalog />} />
                    <Route path="/cart" element={<Cart />} />
                    <Route path="/profile" element={<UserProfile />} />
                    <Route path="/" element={<IndexPage />} />

                    <Route path="*" element={<NotFoundPage />} /> {/* 404 страница */}
                  </Routes>
                </main>
                <FloatingIcons />
              </div>
              <Footer />
            </Suspense>
          </div>
        </Router>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
