import React, { createContext, useState, useContext, useCallback } from 'react';

export const AuthContext = createContext({
  token: null,
  user: null,
  isLoading: false,
  error: null,
  login: () => {},
  logout: () => {},
  register: () => {},
  updateProfile: () => {},
  requestPasswordReset: () => {},
  resetPassword: () => {},
});

const authApi = {
  register: async (username, email, password) => {
    const response = await fetch('/api/users/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, email, password }),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Ошибка регистрации.');
    }
    return await response.json();
  },
  login: async (username, password) => {
    const response = await fetch('/api/users/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Ошибка входа.');
    }
    return await response.json();
  },
  updateProfile: async (token, userData) => {
    const response = await fetch('/api/users/update-profile', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,
      },
      body: JSON.stringify(userData),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Ошибка обновления профиля.');
    }
    return await response.json();
  },
  requestPasswordReset: async (email) => {
    const response = await fetch('/api/users/password-reset', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ email }),
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Ошибка запроса на сброс пароля.');
    }
    return await response.json();
  },
  resetPassword: async (userId, token, newPassword) => {
    const response = await fetch(`/api/users/password-reset/${userId}/${token}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ newPassword }), // Убедитесь, что ключ в JSON соответствует ожидаемому на сервере
    });
    if (!response.ok) {
      const errorData = await response.json();
      throw new Error(errorData.message || 'Ошибка сброса пароля.');
    }
    return await response.json();
  },
};

export const AuthProvider = ({ children }) => {
  const [authState, setAuthState] = useState({
    token: localStorage.getItem('token') || null,
    user: JSON.parse(localStorage.getItem('user')) || null,
    isLoading: false,
    error: null,
  });

  const setAuthInfo = useCallback(({ token, user }) => {
    localStorage.setItem('token', token);
    localStorage.setItem('user', JSON.stringify(user));
    setAuthState({
      token,
      user,
      isLoading: false,
      error: null,
    });
  }, []);

  const clearAuthInfo = useCallback(() => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setAuthState({
      token: null,
      user: null,
      isLoading: false,
      error: null,
    });
  }, []);

  const register = useCallback(async (username, email, password) => {
    try {
      setAuthState({ ...authState, isLoading: true });
      const { token, user } = await authApi.register(username, email, password);
      setAuthInfo({ token, user });
    } catch (error) {
      setAuthState({ ...authState, isLoading: false, error: error.message });
    }
  }, [authState, setAuthInfo]);

  const login = useCallback(async (username, password) => {
    try {
      setAuthState({ ...authState, isLoading: true });
      const { token, user } = await authApi.login(username, password);
      setAuthInfo({ token, user });
    } catch (error) {
      setAuthState({ ...authState, isLoading: false, error: error.message });
    }
  }, [authState, setAuthInfo]);

  const logout = useCallback(() => {
    clearAuthInfo();
  }, [clearAuthInfo]);

  const updateProfile = useCallback(async (userData) => {
    try {
      setAuthState({ ...authState, isLoading: true });
      const data = await authApi.updateProfile(authState.token, userData);
      setAuthInfo({ token: authState.token, user: data.user });
    } catch (error) {
      setAuthState({ ...authState, isLoading: false, error: error.message });
    }
  }, [authState, setAuthInfo]);

  const requestPasswordReset = useCallback(async (email) => {
    setAuthState((prevState) => ({ ...prevState, isLoading: true, error: null }));
    try {
      const response = await fetch('/api/users/password-reset/request', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || 'Не удалось выполнить запрос на сброс пароля.');
      }
      const result = await response.json();
      alert(result.message); // Отображаем сообщение об успешной отправке инструкций (или используйте другой способ уведомления пользователя)
      setAuthState((prevState) => ({ ...prevState, isLoading: false }));
    } catch (error) {
      console.error(error);
      setAuthState((prevState) => ({ ...prevState, isLoading: false, error: error.message }));
    }
  }, []);


  const resetPassword = useCallback(async (email) => {
    try {
      setAuthState({ ...authState, isLoading: true });
      await authApi.resetPassword(email);
      setAuthState({ ...authState, isLoading: false, error: null });
    } catch (error) {
      setAuthState({ ...authState, isLoading: false, error: error.message });
    }
  }, [authState]);

  const contextValue = {
    ...authState,
    login,
    logout,
    register,
    updateProfile,
    resetPassword, // Убедитесь, что используете исправленную версию
    requestPasswordReset,
  };

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
