import React, { useState, useEffect } from 'react';
import { Modal, Button, Form, Alert } from 'react-bootstrap';
import './FurnitureConstructor.css';

const FurnitureConstructor = () => {
  const [size, setSize] = useState('');
  const [color, setColor] = useState('');
  const [material, setMaterial] = useState('');
  const [price, setPrice] = useState(0);
  const [image, setImage] = useState('default_image.jpg');
  const [showModal, setShowModal] = useState(false);
  const [addedToCart, setAddedToCart] = useState(false);

  useEffect(() => {
    updateImage(size, material);
    updatePrice(size, material, color);
  }, [size, material, color]);

  const updateImage = (selectedSize, selectedMaterial) => {
    let imageUrl = `images/${selectedMaterial}_${selectedSize}.jpg`;
    setImage(imageUrl);
  };

  const updatePrice = (selectedSize, selectedMaterial, selectedColor) => {
      let basePrice = 100; // Базовая цена
      let sizePrice = selectedSize === "3.5" ? 50 : selectedSize === "2" ? 30 : selectedSize === "1.5" ? 20 : 10; // Дополнительная стоимость за размер
      let materialPrice = selectedMaterial === "wood" ? 30 : selectedMaterial === "metal" ? 20 : selectedMaterial === "glass" ? 10 : 0; // Стоимость материала
      let colorPrice = selectedColor === "custom" ? 40 : selectedColor ? 10 : 0; // Стоимость за индивидуальный цвет

      let totalPrice = basePrice + sizePrice + materialPrice + colorPrice;
      setPrice(totalPrice);
    };




  const addToCart = () => {
    console.log('Товар добавлен в корзину: ', { size, color, material, price });
    setAddedToCart(true);
    setTimeout(() => setAddedToCart(false), 3000);
  };

  return (
    <div className="furniture-constructor">
      <div className="constructor-image">
        <img src={image} alt="Изображение мебели" onClick={() => setShowModal(true)} />
      </div>

      <div className="constructor-form">
        <h3>Настройте вашу мебель</h3>
        <Form>
          <Form.Group controlId="sizeSelect">
            <Form.Label>Выберите размер</Form.Label>
            <Form.Control as="select" value={size} onChange={e => setSize(e.target.value)}>
              <option value="1">1 м</option>
              <option value="1.5">1.5 м</option>
              <option value="2">2 м</option>
              <option value="3.5">3.5 м</option>
            </Form.Control>
          </Form.Group>

          <Form.Group controlId="materialSelect">
            <Form.Label>Выберите материал</Form.Label>
            <Form.Control as="select" value={material} onChange={e => setMaterial(e.target.value)}>
              <option value="wood">Дерево</option>
              <option value="metal">Металл</option>
              <option value="glass">Стекло</option>
            </Form.Control>
          </Form.Group>

          <Form.Group controlId="colorSelect">
            <Form.Label>Выберите цвет</Form.Label>
            <Form.Control as="select" value={color} onChange={e => setColor(e.target.value)}>
              <option value="red">Красный</option>
              <option value="blue">Синий</option>
              <option value="green">Зеленый</option>
              <option value="custom">Индивидуальный цвет</option>
            </Form.Control>
          </Form.Group>

          <p className="price">Цена: {price} руб.</p>
          <Button variant="success" onClick={addToCart}>Добавить в корзину</Button>
        </Form>
      </div>

      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Детали продукта</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <img src={image} alt="Изображение мебели" className="img-fluid" />
          <p>Размер: {size}</p>
          <p>Материал: {material}</p>
          <p>Цвет: {color}</p>
          <p>Цена: {price} руб.</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>Закрыть</Button>
          <Button variant="primary" onClick={addToCart}>Добавить в корзину</Button>
        </Modal.Footer>
      </Modal>

      {addedToCart && <Alert variant="success">Товар добавлен в корзину!</Alert>}
    </div>
  );
};

export default FurnitureConstructor;
