import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function IntroSection() {
    const [introData, setIntroData] = useState({
        title: '',
        paragraphs: [], // Предполагается, что это массив объектов с текстом абзацев
        images: []      // Предполагается, что это массив URL-адресов изображений
    });
    const [error, setError] = useState(null);

    // Асинхронная функция для загрузки данных
    const fetchData = async () => {
        try {
            const response = await fetch('http://localhost:3000/api/introSection');
            if (!response.ok) {
                throw new Error(`HTTP error! status: ${response.status}`);
            }
            const data = await response.json();

            setIntroData({
                title: data.title,
                paragraphs: data.paragraphs || [],
                images: data.images || [] 
            });
        } catch (error) {
            console.error('Error fetching data:', error);
            setError(error.message);
        }
    };

    useEffect(() => {
        fetchData();
    }, []);

    // Условный рендеринг должен быть внутри тела компонента
    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            <h2>{introData.title || 'Портфолио'}</h2>
            <div className="gallery">
                {introData.images.map((img, index) => (
                    <div className="image-container" key={index}>
                        <img src={img} alt={`Изображение ${index + 1}`} />
                        <h3 className="image-text">{introData.paragraphs[index]?.header}</h3>
                    </div>
                ))}
            </div>
        </section>
    );
}

export default IntroSection;
