import React, { useState } from 'react';
import'./FurnitureConstructor';

const FurnitureConstructor = () => {
  const [size, setSize] = useState('');
  const [color, setColor] = useState('');
  const [material, setMaterial] = useState('');
  const [price, setPrice] = useState(0);
  const [image, setImage] = useState('default_image.jpg'); // URL изображения по умолчанию

  const handleSizeChange = (e) => {
    setSize(e.target.value);
    updatePrice(e.target.value, material, color);
    updateImage(e.target.value, material);
  };

  const handleColorChange = (e) => {
    setColor(e.target.value);
    updatePrice(size, material, e.target.value);
  };

  const handleMaterialChange = (e) => {
    setMaterial(e.target.value);
    updatePrice(size, e.target.value, color);
    updateImage(size, e.target.value);
  };

  const updateImage = (selectedSize, selectedMaterial) => {
    let imageUrl = `images/${selectedMaterial}_${selectedSize}.jpg`;
    setImage(imageUrl);
  };

  const updatePrice = (selectedSize, selectedMaterial, selectedColor) => {
    let basePrice = 100; // Базовая цена
    let sizePrice = selectedSize === "3.5" ? 50 : 20; // Дополнительная стоимость за размер
    let materialPrice = selectedMaterial === "wood" ? 30 : selectedMaterial === "metal" ? 20 : 10; // Стоимость материала
    let colorPrice = selectedColor === "custom" ? 40 : 10; // Стоимость за индивидуальный цвет

    let totalPrice = basePrice + sizePrice + materialPrice + colorPrice;
    setPrice(totalPrice);
  };

  return (
    <div className="furniture-constructor">
      <div className="constructor-image">
        <img src={image} alt="Изображение мебели" />
      </div>

      <div className="constructor-form">
        <h3>Настройте вашу мебель</h3>

        <label>Выберите размер:</label>
        <select value={size} onChange={handleSizeChange}>
          <option value="">Выберите размер</option>
          <option value="1">1 м</option>
          <option value="1.5">1.5 м</option>
          <option value="2">2 м</option>
          <option value="3.5">3.5 м</option>
        </select>

        <label>Выберите материал:</label>
        <select value={material} onChange={handleMaterialChange}>
          <option value="">Выберите материал</option>
          <option value="wood">Дерево</option>
          <option value="metal">Металл</option>
          <option value="glass">Стекло</option>
        </select>

        <label>Выберите цвет:</label>
        <select value={color} onChange={handleColorChange}>
          <option value="">Выберите цвет</option>
          <option value="red">Красный</option>
          <option value="blue">Синий</option>
          <option value="green">Зеленый</option>
          <option value="custom">Индивидуальный цвет</option>
        </select>

        <p>Цена: {price} руб.</p>

        <button>Добавить в корзину</button>
      </div>
    </div>
  );
};

export default FurnitureConstructor;
