import React, { createContext, useContext, useReducer, useEffect } from 'react';

export const AuthContext = createContext();

const initialState = {
  user: null,
  token: null, // Инициализируем токен как null
  isLoading: false,
  error: null,
};

const authReducer = (state, action) => {
  switch (action.type) {
    case 'REQUEST_LOGIN':
      console.log('Attempting to log in...'); // Логируем попытку входа
      return {
        ...state,
        isLoading: true,
        error: null,
      };
    case 'LOGIN_SUCCESS':
      console.log('Login successful:', action.payload.user); // Логируем успешный вход
      localStorage.setItem('token', action.payload.token); // Сохраняем токен при успешном входе
      return {
        ...state,
        user: action.payload.user,
        token: action.payload.token,
        isLoading: false,
        error: null,
      };
    case 'LOGOUT':
      console.log('Logging out...'); // Логируем выход
      localStorage.removeItem('token'); // Удаляем токен при выходе
      return {
        ...state,
        user: null,
        token: null,
        error: null,
      };
    case 'LOGIN_ERROR':
      console.error('Login error:', action.payload); // Логируем ошибку входа
      return {
        ...state,
        isLoading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};

export const AuthProvider = ({ children }) => {
  const [state, dispatch] = useReducer(authReducer, initialState);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Логируем наличие токена
      console.log('Token found in localStorage:', token);
      // Здесь можно добавить запрос к API для проверки токена или получения данных пользователя
      dispatch({ type: 'LOGIN_SUCCESS', payload: { token } });
    }
  }, []);

  const login = async (username, password) => {
    dispatch({ type: 'REQUEST_LOGIN' });
    try {
      const response = await fetch('http://localhost:3000/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify({ username, password }),
      });

      if (response.ok) {
        const data = await response.json();
        dispatch({ type: 'LOGIN_SUCCESS', payload: data });
      } else {
        const errorData = await response.text();
        throw new Error(errorData || 'Не удалось войти в систему');
      }
    } catch (error) {
      dispatch({ type: 'LOGIN_ERROR', payload: error.toString() });
    }
  };

  const logout = () => {
    dispatch({ type: 'LOGOUT' });
  };

  const authContextValue = {
    ...state,
    login,
    logout,
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
