import React, { createContext, useState, useCallback } from 'react';

// Создаем контекст для аутентификации
export const AuthContext = createContext({
  currentUser: null,
  login: async () => {},
  logout: () => {}
});

// Компонент провайдера, который будет обертывать ваше приложение или его части
export const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);

  // Функция для входа пользователя
  const login = useCallback(async (username, password) => {
    try {
      // Здесь должен быть код для отправки запроса на бэкенд и получения токена
      const response = await fetch('/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        throw new Error('Ошибка входа');
      }

      const { token, user } = await response.json();
      setCurrentUser(user); // Устанавливаем текущего пользователя
      localStorage.setItem('token', token); // Сохраняем токен в localStorage
    } catch (error) {
      console.error('Ошибка входа:', error);
      throw error; // Пробрасываем ошибку для обработки на более высоком уровне (например, в компоненте формы входа)
    }
  }, []);

  // Функция для выхода пользователя
  const logout = useCallback(() => {
    setCurrentUser(null); // Очищаем текущего пользователя
    localStorage.removeItem('token'); // Удаляем токен из localStorage
  }, []);

  // Объект контекста, который будет содержать текущего пользователя и функции для входа и выхода
  const contextValue = {
    currentUser,
    login,
    logout,
  };

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};
