import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert, Form } from 'react-bootstrap';
import { useCart } from '../CartContext/CartContext';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import './Catalog.css';

function Catalog() {
    console.log('Is HomePage:', isHomePage);
    const { addToCart } = useCart();
    const [products, setProducts] = useState([]);
    const [addedToCart, setAddedToCart] = useState(false);
    const [error, setError] = useState(null);
    const [selectedCategory, setSelectedCategory] = useState('Все');
    const [currentPage, setCurrentPage] = useState(1);
    const itemsPerPage = isHomePage ? 3 : 10;
    const isHomePage = location.pathname === '/';

    useEffect(() => {
        const fetchProducts = async () => {
            try {

                const token = localStorage.getItem('token');
                const response = await fetch('/api/products', {
                    headers: token ? { 'Authorization': `Bearer ${token}` } : {},
                });

                if (!response.ok) {
                    throw new Error(`Ошибка HTTP, статус: ${response.status}`);
                }
                const data = await response.json();
                if (Array.isArray(data)) {
                    setProducts(data);
                } else {
                    throw new Error('Полученные данные не являются массивом');
                }
            } catch (error) {
                console.error('Ошибка при получении данных:', error);
                setError(error.message);
            }
        };

        fetchProducts();
    }, []);

    const handleAddToCart = (product) => {
        addToCart(product);
        setAddedToCart(true);
        setTimeout(() => setAddedToCart(false), 2000);
    };

    // Фильтрация и пагинация применяются только если !isHomePage
    const filteredProducts = !isHomePage ? products.filter(product =>
        selectedCategory === 'Все' || product.category === selectedCategory
    ) : products;

    const paginatedProducts = filteredProducts.slice((currentPage - 1) * itemsPerPage, currentPage * itemsPerPage);
    const totalPages = Math.ceil(filteredProducts.length / itemsPerPage);

    const handleChangeCategory = (e) => {
        setSelectedCategory(e.target.value);
        setCurrentPage(1); // Сброс к первой странице при смене категории
    };

    if (error) {
        return <Alert variant="danger">Ошибка: {error}</Alert>;
    }

    return (
        <Container className="catalog-container my-5">
            {addedToCart && <Alert variant="success">Товар добавлен в корзину!</Alert>}
            <Row>
                <Col xs={12} className="text-center mb-4">
                    <h2>Каталог товаров</h2>
                    <Form.Select aria-label="Выберите категорию" onChange={handleChangeCategory} value={selectedCategory}>
                        <option value="Все">Все</option>
                        <option value="Кровать">Кровать</option>
                        <option value="Кухня">Кухня</option>
                    </Form.Select>
                </Col>
            </Row>
            <Row className="justify-content-center">
                {paginatedProducts.map(product => (
                    <Col key={product.id} xs={12} sm={6} md={4} lg={3} className="mb-4 d-flex align-items-stretch">
                        <Card className="product-card">
                            <Card.Img variant="top" src={product.image} alt={product.title} />
                            <Card.Body className="d-flex flex-column">
                                <Card.Title>{product.title}</Card.Title>
                                <Card.Text className="mt-auto">{`${product.price} Р`}</Card.Text>
                                <Button variant="success" className="mt-auto" onClick={() => handleAddToCart(product)}>Купить</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
            {isHomePage ? (
                <Row className="justify-content-center">
                    <Col xs={12} className="text-center">
                        <Button variant="outline-primary" as={Link} to="/catalog">Посмотреть все товары</Button>
                    </Col>
                </Row>
            ) : (
                <Row className="justify-content-center">
                    {[...Array(totalPages).keys()].map(page => (
                        <Col key={page} xs="auto">
                            <Button variant={currentPage === page + 1 ? "dark" : "light"} onClick={() => setCurrentPage(page + 1)}>{page + 1}</Button>
                        </Col>
                    ))}
                </Row>
            )}
        </Container>
    );
}

export default Catalog;
