import React, { useState } from 'react';
import './Catalog.css';
import img1 from '../../Image/img1.png'; 
import img2 from '../../Image/img2.png';
import img3 from '../../Image/img3.png';

function Catalog() {
  const products = [
    { id: 1, image: img3 , title: "Продукт 1", description: "Описание продукта 1", category: "Категория 1" },
    { id: 2, image: img1, title: "Продукт 2", description: "Описание продукта 2", category: "Категория 2" },
    { id: 3, image: img2, title: "Продукт 2", description: "Описание продукта 2", category: "Категория 3" },
    { id: 4, image: img1, title: "Продукт 2", description: "Описание продукта 2", category: "Категория 2" },
    { id: 5, image: img1, title: "Продукт 2", description: "Описание продукта 2", category: "Категория 2" },
    // Добавьте другие продукты здесь
  ];

  const categories = ["Все", ...new Set(products.map(product => product.category))];

  const [selectedCategory, setSelectedCategory] = useState("Все");

  const filteredProducts = products.filter(product => 
    selectedCategory === "Все" ? true : product.category === selectedCategory
  );

  return (
    <section className="catalog">
      <div className="category-filter">
        <label htmlFor="category">Выберите категорию: </label>
        <select 
          id="category" 
          value={selectedCategory} 
          onChange={e => setSelectedCategory(e.target.value)}
        >
          {categories.map(category => (
            <option key={category} value={category}>{category}</option>
          ))}
        </select>
      </div>

      {filteredProducts.map(product => (
        <div key={product.id} className="product-card">
          <img src={product.image} alt={product.title} />
          <div className="product-description">
            <h3 className="product-title">{product.title}</h3>
            <p className="product-info">{product.description}</p>
          </div>
        </div>
      ))}
    </section>
  );
}

export default Catalog;
