import React, { useEffect, useState } from 'react';
import { fetchCustomizations } from '../API/customizationsAPI'; // Проверьте пути
import { useFurniture } from '../context/FurnitureContext';

const CustomizationSelector = () => {
    const { customizations, setCustomizations } = useFurniture();
    const [availableCustomizations, setAvailableCustomizations] = useState([]);

    useEffect(() => {
        const initCustomizations = async () => {
            const fetchedCustomizations = await fetchCustomizations();
            setAvailableCustomizations(fetchedCustomizations);
        };
        initCustomizations();
    }, []);

    const handleChange = (e) => {
        const { name, checked } = e.target;
        setCustomizations(prev => ({ ...prev, [name]: checked }));
    };

    return (
        <div className="selector customization-selector">
            <h3>Выберите дополнительные опции</h3>
            {availableCustomizations.map(customization => (
                <label key={customization.id}>
                    <input 
                        type="checkbox" 
                        name={customization.name} 
                        checked={!!customizations[customization.name]} 
                        onChange={handleChange} 
                    />
                    {customization.name}
                </label>
            ))}
        </div>
    );
};

export default CustomizationSelector;
