import React from 'react';
import { useCart } from '../CartContext/CartContext'; 
import CartItem from "../Cart/CartItem/CartItem";
// import PlaceOrderComponent from '../PlaceOrder/PlaceOrder';
import "./Cart.css";

function Cart() {
  const { cartItems, updateQuantity, removeItem, orderStatus, placeOrder } = useCart(); 
  const totalCost = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="cart">
      <h2>Ваша корзина</h2>
      {orderStatus === 'success' && <div className="order-success-message">Заказ успешно оформлен!</div>}
      {cartItems.length > 0 ? (
        <>
          {cartItems.map(item => (
            <CartItem 
              key={item.id}
              item={item}
              updateQuantity={updateQuantity}
              removeItem={removeItem}
            />
          ))}
          <div className="total-cost">
            <strong>Общая стоимость: </strong> 
            {totalCost.toLocaleString('ru-RU', { style: 'currency', currency: 'RUB' })}
          </div>
          <button className="place-order-button" onClick={placeOrder}>Оформить заказ</button>
        </>
      ) : (
        <p>Ваша корзина пуста.</p>
      )}
    </div>
  );
}

export default Cart;
