import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Alert } from 'react-bootstrap';
import { useCart } from '../CartContext/CartContext';
import './Catalog.css';

function Catalog() {
    const { addToCart } = useCart();
    const [products, setProducts] = useState([]);
    const [addedToCart, setAddedToCart] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const response = await fetch('/products'); // Измените на ваш URL API
                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setProducts(data);
            } catch (error) {
                console.error('Error fetching data:', error);
                setError(error.message);
            }
        };

        fetchProducts();
    }, []);

    const handleAddToCart = (product) => {
        addToCart(product);
        setAddedToCart(true);
        setTimeout(() => setAddedToCart(false), 2000);
    };

    return (
        <Container className="catalog-container my-5">
            {addedToCart && <Alert variant="success">Товар добавлен в корзину!</Alert>}
            {error && <Alert variant="danger">Ошибка: {error}</Alert>}
            <Row>
                <Col xs={12} className="text-center mb-4">
                    <h2>Каталог товаров</h2>
                </Col>
            </Row>
            <Row className="justify-content-center">
                {products.map(product => (
                    <Col xs={12} sm={6} md={4} lg={3} key={product.id} className="mb-4 d-flex align-items-stretch">
                        <Card className="product-card">
                            <Card.Img variant="top" src={product.image} alt={product.title} />
                            <Card.Body className="d-flex flex-column">
                                <Card.Title>{product.title}</Card.Title>
                                <Card.Text className="mt-auto">
                                    {`${product.price} Р`}
                                </Card.Text>
                                <Button variant="success" className="mt-auto" onClick={() => handleAddToCart(product)}>
                                    Купить
                                </Button>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
            <Row>
                <Col className="text-center">
                    <Button variant="outline-primary" className="mt-3">Показать больше</Button>
                </Col>
            </Row>
        </Container>
    );
}

export default Catalog;
