import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const CartContext = createContext();

export const useCart = () => useContext(CartContext);

const CartProvider = ({ children }) => {
    const navigate = useNavigate();
    const [cartItems, setCartItems] = useState([]);
    const [orderStatus, setOrderStatus] = useState(null);
    const [isUserAuthenticated, setIsUserAuthenticated] = useState(false);

    useEffect(() => {
        // Проверка аутентификации и обновление корзины
        const token = localStorage.getItem('token');
        setIsUserAuthenticated(!!token);
        // Загружаем данные корзины из localStorage или устанавливаем пустой массив
        const savedCart = localStorage.getItem('cart');
        setCartItems(savedCart ? JSON.parse(savedCart) : []);
    }, []);

    useEffect(() => {
        // Сохранение текущего состояния корзины в localStorage
        localStorage.setItem('cart', JSON.stringify(cartItems));
    }, [cartItems]);


  const addToCart = (product) => {
    setCartItems(prevItems => {
      const itemIndex = prevItems.findIndex(item => item.id === product.id);
      if (itemIndex !== -1) {
        const updatedItems = [...prevItems];
        updatedItems[itemIndex].quantity += 1;
        return updatedItems;
      }
      return [...prevItems, { ...product, quantity: 1 }];
    });
  };

  const updateQuantity = (productId, quantity) => {
    setCartItems(prevItems =>
      prevItems.map(item =>
        item.id === productId ? { ...item, quantity: Math.max(1, quantity) } : item
      )
    );
  };

  const removeItem = (productId) => {
    setCartItems(prevItems => prevItems.filter(item => item.id !== productId));
  };

  const placeOrder = async () => {
    if (!isUserAuthenticated) {
      navigate('/login');
      return;
    }

    if (cartItems.length === 0) {
      setOrderStatus('empty');
      return;
    }

    setOrderStatus('processing');
    const token = localStorage.getItem('token');

    try {
      const response = await fetch('/api/orders/placeOrder', {
        method: 'POST',
        body: JSON.stringify(cartItems), // Изменено для отправки массива напрямую
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
      });

      if (response.status === 429) {
        setOrderStatus('too-many-requests');
        return;
      }

      if (!response.ok) {
        throw new Error(`Ошибка: ${response.status}`);
      }

      setCartItems([]);
      setOrderStatus('success');
    } catch (error) {
      console.error('Ошибка при оформлении заказа:', error);
      setOrderStatus('error');
    }
  };

  return (
    <CartContext.Provider value={{
        cartItems,
        addToCart,
        updateQuantity,
        removeItem,
        placeOrder,
        orderStatus,
        setOrderStatus,
        isUserAuthenticated
    }}>
        {children}
    </CartContext.Provider>
);
};

export default CartProvider;
