import React, { createContext, useContext, useState, useCallback } from 'react';

export const AuthContext = createContext(null);

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(localStorage.getItem('token'));
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchUserData = useCallback(async () => {
    setIsLoading(true);
    try {
      const response = await fetch('/api/users/me', {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'application/json',
        },
      });

      if (!response.ok) {
        throw new Error('Ошибка аутентификации. Пожалуйста, войдите снова.');
      }

      // Проверка на пустой ответ
      const text = await response.text();
      const userData = text ? JSON.parse(text) : null;

      if (userData) {
        setUser(userData);
      } else {
        throw new Error('Пустой ответ от сервера');
      }
    } catch (error) {
      setError(error.message);
      setToken(null);
      localStorage.removeItem('token');
      setUser(null);
    } finally {
      setIsLoading(false);
    }
  }, [token]);


  // Функция для входа
  const login = async (username, password) => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await fetch('/api/users/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const data = await response.json();
      if (!response.ok) {
        throw new Error(data.message || 'Произошла ошибка при аутентификации.');
      }

      setToken(data.token);
      localStorage.setItem('token', data.token);
      await fetchUserData();
    } catch (error) {
      setError(error.message);
      setUser(null);
    } finally {
      setIsLoading(false);
    }
  };

  // Функция для выхода
  const logout = () => {
    setUser(null);
    setToken(null);
    localStorage.removeItem('token');
    setError(null);
  };

  // Объект, который будет доступен через контекст
  const authContextValue = {
    user,
    token,
    isLoading,
    error,
    login,
    logout,
  };

  return (
    <AuthContext.Provider value={authContextValue}>
      {children}
    </AuthContext.Provider>
  );
};
