import React, { useState } from 'react';

const ContentEditor = () => {
  const [content, setContent] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();
    setIsLoading(true);
    setMessage('');

    try {
      const response = await fetch('/api/content/save', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          // Добавьте необходимые заголовки, например, токен авторизации
        },
        body: JSON.stringify({ content })
      });

      const data = await response.json();

      if (response.ok) {
        setMessage('Контент успешно сохранен');
      } else {
        setMessage(data.message || 'Ошибка при сохранении контента');
      }
    } catch (error) {
      setMessage('Ошибка при отправке запроса');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="content-editor">
      <h3>Редактор контента</h3>
      <form onSubmit={handleSubmit}>
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows="10"
          cols="50"
          className="form-control"
        />
        <button type="submit" className="btn btn-primary mt-2" disabled={isLoading}>
          Сохранить
        </button>
      </form>
      {message && <div className="alert">{message}</div>}
    </div>
  );
};

export default ContentEditor;
