import React, { createContext, useState, useContext, useCallback } from 'react';

export const AuthContext = createContext(null);

const authApi = {
  register: async (username, email, password) => {
    const response = await fetch('/api/users/register', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, email, password }),
    });
    return response;
  },
  login: async (username, password) => {
    const response = await fetch('/api/users/login', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ username, password }),
    });
    return response;
  },
};

export const AuthProvider = ({ children }) => {
  const [authState, setAuthState] = useState({
    token: null,
    user: null,
    isLoading: false,
    error: null,
  });

  const updateAuthState = useCallback((updates) => {
    setAuthState((prevState) => ({ ...prevState, ...updates }));
  }, []);

  const register = async (username, email, password) => {
    updateAuthState({ isLoading: true, error: null });
    try {
      const response = await authApi.register(username, email, password);

      if (!response.ok) {
        const errMessage = await response.text();
        throw new Error(errMessage || 'Registration failed');
      }

      const { token, user } = await response.json();
      updateAuthState({ token, user, isLoading: false });
    } catch (error) {
      updateAuthState({ isLoading: false, error: error.message });
    }
  };

  const login = async (username, password) => {
    updateAuthState({ isLoading: true, error: null });
    try {
      const response = await authApi.login(username, password);

      if (!response.ok) {
        const errMessage = await response.text();
        throw new Error(errMessage || 'Login failed');
      }

      const { token, user } = await response.json();
      updateAuthState({ token, user, isLoading: false });
    } catch (error) {
      updateAuthState({ isLoading: false, error: error.message });
    }
  };

  const logout = useCallback(() => {
    updateAuthState({ token: null, user: null, isLoading: false, error: null });
  }, []);

  const contextValue = { ...authState, login, logout, register };

  return (
    <AuthContext.Provider value={contextValue}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);
