import React, { useState, useEffect } from 'react';
import './IntroSection.css';

function ModalWindow({ isOpen, onClose, children }) {
    if (!isOpen) return null;

    return (
        <div className="modal-backdrop" onClick={onClose}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                {children}
                <button className="close-button" onClick={onClose}>Закрыть</button>
            </div>
        </div>
    );
}

function IntroSection() {
    const [introData, setIntroData] = useState({ title: '', sections: [], images: [] });
    const [selectedSection, setSelectedSection] = useState(null);
    const [modalVisible, setModalVisible] = useState(false);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const token = localStorage.getItem('token');
                const response = await fetch('/api/introSection', {
                    headers: token ? { 'Authorization': `Bearer ${token}` } : {},
                });

                if (!response.ok) {
                    throw new Error(`HTTP error! status: ${response.status}`);
                }
                const data = await response.json();
                setIntroData({
                    title: data.title,
                    sections: data.paragraphs.sections,
                    images: data.images.images
                });
            } catch (error) {
                console.error('Error fetching data:', error);
                setError(error.message);
            }
        };

        fetchData();
    }, []);

    const openModal = (index) => {
        setSelectedSection(introData.sections[index]);
        setModalVisible(true);
    };

    const closeModal = () => {
        setModalVisible(false);
    };

    if (error) {
        return <div>Error loading Intro Section: {error}</div>;
    }

    return (
        <section className="intro-section">
            <h2>{introData.title || 'О нашей компании'}</h2>
            <div className="gallery">
                {introData.images.map((img, index) => (
                    <div key={index} className="image-block">
                        <img src={img} alt={`Изображение ${index + 1}`} />
                        <p>{img.header}</p>
                        <button onClick={() => openModal(index)}>Узнать больше</button>
                    </div>
                ))}
            </div>
            <ModalWindow isOpen={modalVisible} onClose={closeModal}>
                {selectedSection && (
                    <>
                        <h3>{selectedSection.header}</h3>
                        <p>{selectedSection.content}</p>
                    </>
                )}
            </ModalWindow>
        </section>
    );
}

export default IntroSection;
