import React from 'react';
import { useCart } from '../CartContext/CartContext'; 
import CartItem from "../Cart/CartItem/CartItem";
import PlaceOrderComponent from '../PlaceOrder/PlaceOrder';
import "./Cart.css";

function Cart() {
  const { cartItems, updateQuantity, removeItem } = useCart(); 
  const totalCost = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

  return (
    <div className="cart-container">
      <h2 className="cart-title">Ваша корзина</h2>
      <div className="cart-items-list">
        {cartItems.length > 0 ? (
          <>
            {cartItems.map(item => (
              <CartItem 
                key={item.id}
                item={item}
                updateQuantity={updateQuantity}
                removeItem={removeItem}
              />
            ))}
            <div className="cart-checkout">
              <div className="cart-total">
                <strong>Общая стоимость:</strong>
                <span>{totalCost.toLocaleString('ru-RU', { style: 'currency', currency: 'RUB' })}</span>
              </div>
              <PlaceOrderComponent />
            </div>
          </>
        ) : (
          <p className="cart-empty">Ваша корзина пуста.</p>
        )}
      </div>
    </div>
  );
}

export default Cart;
