import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../AuthContext/AuthContext'; // Импортируем хук для использования контекста аутентификации

const AuthForm = () => {
  const navigate = useNavigate(); // Хук для программного перенаправления
  const [formType, setFormType] = useState('login'); // Состояние для отслеживания типа формы: вход или регистрация
  const [credentials, setCredentials] = useState({ username: '', password: '', email: '', newPassword: '' }); // Состояние для учетных данных пользователя
  const { login, register, isLoading, error } = useAuth(); // Используем функции из контекста аутентификации

  // Обработчик изменений в полях формы
  const handleChange = (e) => {
    const { name, value } = e.target; // Получаем имя поля и его значение
    setCredentials(prevCredentials => ({ ...prevCredentials, [name]: value })); // Обновляем состояние с учетными данными пользователя
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    if (isLoading) return;

    try {
      if (formType === 'login') {
        const response = await login(credentials.username, credentials.password);
        if (response && response.user) { // Добавлена проверка на наличие ответа и пользователя
          const { user } = response; // Теперь безопасно деструктурируем user
          navigate(user.role === 'admin' ? '/adminPanel' : '/userProfile');
        } else {
          throw new Error('Login failed, no user data received'); // Обработка ошибки, если данных нет
        }
      } else if (formType === 'register') {
        const response = await register(credentials.username, credentials.password, credentials.email);
        if (response && response.user) {
          const { user } = response;
          alert('Вы успешно зарегистрированы на сайте!');
          navigate(user.role === 'admin' ? '/adminPanel' : '/userProfile');
        } else {
          throw new Error('Registration failed, no user data received');
        }
      } // Пропускаем 'reset', так как он не влияет на деструктуризацию 'user'
    } catch (error) {
      alert(`Произошла ошибка: ${error.message}`); // Уведомление пользователя об ошибке
    }
  };

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2 className="auth-form-title">
          {formType === 'login' ? 'Вход в систему' : formType === 'register' ? 'Регистрация' : 'Сброс пароля'}
        </h2>
        <form onSubmit={handleSubmit}>
          {formType === 'register' && (
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                type="email"
                id="email"
                name="email"
                value={credentials.email}
                onChange={handleChange}
                required={formType === 'register'}
                disabled={isLoading}
              />
            </div>
          )}
          <div className="form-group">
            <label htmlFor="username">Имя пользователя</label>
            <input
              type="text"
              id="username"
              name="username"
              value={credentials.username}
              onChange={handleChange}
              required
              disabled={isLoading}
            />
          </div>
          {formType !== 'reset' && (
            <div className="form-group">
              <label htmlFor="password">Пароль</label>
              <input
                type="password"
                id="password"
                name="password"
                value={credentials.password}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>
          )}
          {formType === 'reset' && (
            <div className="form-group">
              <label htmlFor="newPassword">Новый пароль</label>
              <input
                type="password"
                id="newPassword"
                name="newPassword"
                value={credentials.newPassword}
                onChange={handleChange}
                required
                disabled={isLoading}
              />
            </div>
          )}
          {error && <div className="alert alert-danger" role="alert">{error}</div>}
          <button type="submit" className="gradient-button">
            {isLoading ? 'Загрузка...' : formType === 'login' ? 'Войти' : formType === 'register' ? 'Зарегистрироваться' : 'Сбросить пароль'}
          </button>
          <button type="button" className="toggle-button" onClick={() => setFormType(formType === 'login' ? 'register' : 'login')}>
            {formType === 'login' ? 'Зарегистрироваться' : 'Войти'}
          </button>
          {formType !== 'reset' && (
            <button type="button" className="toggle-button" onClick={() => setFormType('reset')}>
              Забыли пароль?
            </button>
          )}
        </form>
      </div>
    </div>
  );
};

export default AuthForm;
