import React, { useState, useEffect } from 'react';
import './IntroSection.css';
import '../../index.css';

function IntroSection() {
  const [introData, setIntroData] = useState({ paragraphs: [], images: [] });
  const [error, setError] = useState(null); // Добавлено состояние для ошибок

  useEffect(() => {
    fetch('/api/intro-section')
      .then(response => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then(data => {
        setIntroData(data);
        console.log('Fetched data:', data);
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        // Обновление состояния ошибки для отображения в интерфейсе
        setError(`Failed to fetch data: ${error.message}`);
      });
  }, []);

  if (error) {
    // Отображение ошибки, если она есть
    return <div>Error loading Intro Section: {error}</div>;
  }

  return (
    <section className="intro-section">
      <h2>{introData.title || 'О нашей компании'}</h2>
      {introData.paragraphs.map((paragraph, index) => (
        <p key={index}>{paragraph}</p>
      ))}
      <div className="gallery">
        {introData.images.map((img, index) => (
          <img key={index} src={img} alt={`Изображение ${index + 1}`} />
        ))}
      </div>
    </section>
  );
}

export default IntroSection;
