import React, { useState, useContext } from 'react';
import { AuthContext } from '../AuthContext/AuthContext';
import 'bootstrap/dist/css/bootstrap.min.css';
import './AuthForm.css';

const AuthForm = () => {
  // Используем состояния для управления формой и отображения ошибок
  const [isLogin, setIsLogin] = useState(true); // Переключение между режимами входа и регистрации
  const [username, setUsername] = useState(''); // Состояние для имени пользователя
  const [password, setPassword] = useState(''); // Состояние для пароля
  const [localError, setLocalError] = useState(''); // Локальное состояние для отображения ошибок валидации

  // Доступ к контексту аутентификации
  const { user, isLoading, error, login, register, logout } = useContext(AuthContext);

  // Валидация введенных данных
  const validateForm = () => {
    if (!/^[a-zA-Z0-9_]+$/.test(username)) {
      return 'Имя пользователя может содержать только латинские буквы, цифры и подчеркивания.';
    }
    if (!/^(?=.*[A-Za-z])(?=.*\d)[A-Za-z\d]{6,}$/.test(password)) {
      return 'Пароль должен быть не менее 6 символов и содержать хотя бы одну цифру.';
    }
    return '';
  };

  // Обработчик отправки формы
  const handleSubmit = async (e) => {
    e.preventDefault(); // Предотвращаем перезагрузку страницы
    const validationError = validateForm();
    if (validationError) {
      setLocalError(validationError); // Устанавливаем ошибку валидации, если она есть
      return;
    }

    try {
      // Выполняем вход или регистрацию в зависимости от режима
      isLogin ? await login(username, password) : await register(username, password);
      setUsername(''); // Очищаем форму после успешного входа/регистрации
      setPassword('');
      setLocalError(''); // Очищаем ошибки после успешного входа/регистрации
    } catch (err) {
      // Ошибки аутентификации обрабатываются на уровне контекста, здесь их обрабатывать не нужно
    }
  };

  // Обработчик выхода из системы
  const handleLogout = () => {
    logout(); // Вызываем функцию выхода из контекста аутентификации
    setUsername('');
    setPassword('');
    setLocalError('');
  };

  // Выбор ошибки для отображения пользователю
  const displayError = localError || error;

  return (
    <div className="auth-form-wrapper">
      <div className="auth-form-container">
        <h2>{isLogin ? 'Вход в систему' : 'Регистрация'}</h2>
        {/* Отображение ошибок */}
        {displayError && <div className="alert alert-danger">{displayError}</div>}
        {user ? (
          // Интерфейс для аутентифицированного пользователя
          <div>
            <div className="alert alert-success">Вы вошли как {user.name}</div>
            <button onClick={handleLogout} className="btn btn-danger">Выйти</button>
          </div>
        ) : (
          // Форма для входа или регистрации
          <form onSubmit={handleSubmit}>
            <div className="mb-3">
              <label htmlFor="username">Имя пользователя:</label>
              <input
                type="text"
                className="form-control"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                required
              />
            </div>
            <div className="mb-3">
              <label htmlFor="password">Пароль:</label>
              <input
                type="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </div>
            <button type="submit" className="btn btn-primary" disabled={isLoading}>
              {isLoading ? 'Загрузка...' : isLogin ? 'Войти' : 'Зарегистрироваться'}
            </button>
          </form>
        )}
        {!user && (
          // Ссылка для переключения между регистрацией и входом
          <button onClick={() => setIsLogin(!isLogin)} className="btn btn-link">
            {isLogin ? 'Нет аккаунта? Зарегистрируйтесь' : 'Уже есть аккаунт? Войдите'}
          </button>
        )}
      </div>
    </div>
  );
};

export default AuthForm;
